(261, 'woo_template', 'a:236:{i:0;a:3:{s:4:"name";s:16:"General Settings";s:4:"icon";s:7:"general";s:4:"type";s:7:"heading";}i:1;a:2:{s:4:"name";s:11:"Quick Start";s:4:"type";s:10:"subheading";}i:2;a:5:{s:4:"name";s:11:"Custom Logo";s:4:"desc";s:63:"Upload a logo for your theme, or specify an image URL directly.";s:2:"id";s:8:"woo_logo";s:3:"std";s:0:"";s:4:"type";s:6:"upload";}i:3;a:5:{s:4:"name";s:14:"Custom Favicon";s:4:"desc";s:78:"Upload a 16px x 16px Png/Gif image that will represent your website''s favicon.";s:2:"id";s:18:"woo_custom_favicon";s:3:"std";s:0:"";s:4:"type";s:6:"upload";}i:4;a:5:{s:4:"name";s:13:"Tracking Code";s:4:"desc";s:117:"Paste your Google Analytics (or other) tracking code here. This will be added into the footer template of your theme.";s:2:"id";s:20:"woo_google_analytics";s:3:"std";s:0:"";s:4:"type";s:8:"textarea";}i:5;a:2:{s:4:"name";s:21:"Subscription Settings";s:4:"type";s:10:"subheading";}i:6;a:5:{s:4:"name";s:7:"RSS URL";s:4:"desc";s:51:"Enter your preferred RSS URL. (Feedburner or other)";s:2:"id";s:12:"woo_feed_url";s:3:"std";s:0:"";s:4:"type";s:4:"text";}i:7;a:5:{s:4:"name";s:10:"E-Mail URL";s:4:"desc";s:67:"Enter your preferred E-mail subscription URL. (Feedburner or other)";s:2:"id";s:19:"woo_subscribe_email";s:3:"std";s:0:"";s:4:"type";s:4:"text";}i:8;a:5:{s:4:"name";s:19:"Contact Form E-Mail";s:4:"desc";s:156:"Enter your E-mail address to use on the Contact Form Page Template. Add the contact form by adding a new page and selecting "Contact Form" as page template.";s:2:"id";s:21:"woo_contactform_email";s:3:"std";s:0:"";s:4:"type";s:4:"text";}i:9;a:2:{s:4:"name";s:15:"Display Options";s:4:"type";s:10:"subheading";}i:10;a:5:{s:4:"name";s:10:"Custom CSS";s:4:"desc";s:62:"Quickly add some CSS to your theme by adding it to this block.";s:2:"id";s:14:"woo_custom_css";s:3:"std";s:0:"";s:4:"type";s:8:"textarea";}i:11;a:5:{s:4:"name";s:18:"Post/Page Comments";s:4:"desc";s:53:"Select if you want to comments on posts and/or pages.";s:2